#include<stdio.h>
#include<unordered_set>
#include<vector>
#include<algorithm>
#include<tuple>
using std::vector;
using std::unordered_multiset;


struct indexed_tree {
	vector<unordered_multiset<int>> tree;
	int k = 1;
	indexed_tree(int n) {
		while (k < n) { k *= 2; }
		tree.resize(k * 3);
	}
	void update(int left, int right,int a) {
		left += k;
		right += k;
		while (1) {
			if (left > right) {
				break;
			}
			if (left == right) {
				tree[left].insert(a);
				break;
			}
			if (left % 2 == 1) {
				tree[left].insert(a);
			}
			if (right % 2 == 0) {
				tree[right].insert(a);
			}
			left = (left + 1) / 2;
			right = (right - 1) / 2;
		}
	}

	int count(int i, int a) {
		int ret = 0;
		int idx = k + i;
		while (idx) {
			ret += tree[idx].count(a);
			ret -= tree[idx].count(-a);
			idx /= 2;
		}
		return ret;
	}
};


using std::tuple;
int main() {
	int n;
	scanf("%d", &n);
	vector<tuple<int, int, int>> q;
	vector<int> T;
	vector<int> X;
	for (int i = 0; i < n; i++) {
		int a, b, c;
		scanf("%d%d%d", &a, &b, &c);
		q.emplace_back(a, b, c);
		T.push_back(b);
		X.push_back(c);
	}
	std::sort(T.begin(), T.end());
	std::sort(X.begin(), X.end());
	T.erase(std::unique(T.begin(), T.end()), T.end());
	X.erase(std::unique(X.begin(), X.end()), X.end());
	indexed_tree tree(n + 5);
	for (int i = 0; i < n; i++) {
		int a, b, c;
		std::tie(a, b, c) = q[i];
		b = std::lower_bound(T.begin(), T.end(), b) - T.begin()+1;
		c = std::lower_bound(X.begin(), X.end(), c) - X.begin()+1;
		if (a == 1) {
			tree.update(b, T.size(), c);
		}
		if (a == 2) {
			tree.update(b, T.size(), -c);
		}
		if (a == 3) {
			printf("%d\n", tree.count(b, c));
		}
	}
}

