typedef long long ll;
typedef pair<int, int> pii;
const int MAXN = 5e5 + 20;
const int MAXM = 1e6 + 20;
const int INF = 0x3f3f3f3f;
const ll MOD = 1e9 + 7;
int n, m;

struct node {
    int x, y;

    bool operator<(const node &A) const {
        return x < A.x;
    }
} p[MAXN];
bool check(){
    int flag=0,sum=0;
    for(int i=1;i<=m;i++){
        if(p[i].x==p[i+1].x){
            if(flag!=0)return false;
            else i++;
        }
        else{
            if(flag==0)sum=p[i].x+p[i].y,flag=1;
            else{
