        scanf("%d%d", &n, &m);
        for (int i = 1; i <= m; i++) {
            scanf("%d%d", &p[i].y, &p[i].x);
        }
        sort(p + 1, p + 1 + m);

        if(check())printf("YES\n");
        else printf("NO\n");
    }


