#include <iostream>
#include <algorithm>
#include <vector>
#include <set>
#include <map>
#include <cmath>
#include <queue>
#include <stack>
#define int long long

using namespace std;

const int mod = 998244353;

inline int add(int a, int b)
{
    if (a + b < mod)
        return a + b;
    return a + b - mod;
}

inline int sub(int a, int b)
{
    if (a - b < 0)
        return a - b + mod;
    return a - b;
}

inline mul(int a, int b)
{
    return (a * b) % mod;
}

int bin_pow(int a, int degree)
{
    if (degree == 0)
        return 1;
    if (degree == 1)
        return a;
    if (degree % 2 == 0)
    {
        int num = bin_pow(a, degree / 2);
        return mul(num, num);
    }
    else
    {
        return mul(a, bin_pow(a, degree - 1));
    }
}

map<int, int> factorize(int n)
{
    map<int, int> res;
    for (int i = 2; i * i <= n; i++)
    {
        while(n % i == 0)
        {
            res[i]++;
            n /= i;
        }
    }
    if (n != 1)
        res[n]++;
    return res;
}

signed main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    int t;
    cin >> t;
    while(t--)
    {
        int n;
        cin >> n;
        map<pair<int, int>, pair<int, int>> ratio_m;
        vector<set<int>> connect(n);
        for (int i1 = 0; i1 < n - 1; i1++)
        {
            int i, j, x, y;
            cin >> i >> j >> x >> y;
            i--, j--;
            ratio_m[{i, j}] = {x, y};
            ratio_m[{j, i}] = {y, x};
            connect[i].insert(j);
            connect[j].insert(i);
        }
        vector<pair<int, int>> have(n, {1, 1});
        queue<int> q;
        q.push(0);
        while(!q.empty())
        {
            int now = q.front();
            q.pop();
            while(!connect[now].empty())
            {
                int beg = *connect[now].begin();
                connect[now].erase(beg);
                connect[beg].erase(now);
                q.push(beg);
                auto p = ratio_m[{now, beg}];
                have[beg] = {mul(have[now].first, p.second), mul(have[now].second, p.first)};
            }
        }
        int res = 1;
        vector<int> must(200000, 0);
        for (int i = 0; i < n; i++)
        {
            map<int, int> f = factorize(have[i].first);
            map<int, int> s = factorize(have[i].second);
            auto it = f.begin();
            while(it != f.end())
            {
                pair<int, int> check = {it->first, it->second};
                int can_del = min(check.second, s[check.first]);
                for (int y = 0; y < can_del; y++)
                {
                    have[i].first /= check.first;
                    have[i].second /= check.first;
                }
                s[check.first] -= check.second;
                it++;
            }
            it = s.begin();
            while(it != s.end())
            {
                pair<int, int> check = {it->first, it->second};
                must[check.first] = max(check.second, must[check.first]);
                it++;
            }
        }
        for (int i = 2; i < 200000; i++)
        {
            res = mul(res, bin_pow(i, must[i]));
        }
        int itog = 0;
        for (int i = 0; i < n; i++)
        {
            itog = add(itog, (res * have[i].first / max(1, have[i].second)) % mod);
        }
        cout << itog << "\n";
    }

}

