#define int long long

using namespace std;

const int mod = 998244353;

inline int add(int a, int b)
{
    if (a + b >= mod)
        return a + b - mod;
    return a + b;
}

inline int sub(int a, int b)
{
    if (a - b < 0)
        return a - b + mod;
    return a - b;
}

inline int mul(int a, int b)
{
    return (a * b) % mod;
}

int bin_pow(int a, int degree)
{
    if (degree == 0)
        return 1;
    if (degree == 1)
        return a;
    if (degree % 2 == 1)
        return mul(a, bin_pow(a, degree - 1));
    int num = bin_pow(a, degree / 2);
    return mul(num, num);
}

int divide(int a, int b)
