}

vector<int> sieve(300000, 0);

void gen_sieve()
{
    sieve[1] = 1;
    for (int i = 2; i < 300000; i++)
    {
        if (!sieve[i])
        {
            sieve[i] = i;
            for (int y = 2; y * i < 300000; y++)
            {
                if (!sieve[y * i])
                    sieve[y * i] = i;
            }
        }
    }
}

vector<int> factorize(int a)
{
    vector<int> res;
    while (a > 1)
    {
        res.push_back(sieve[a]);
        a = a / sieve[a];
    }
    return res;
}

vector<vector<vector<int>>> gr;
vector<int> degrees;
vector<int> now;
vector<pair<int, int>> have;

void dfs(int v, int last)
{
    for (int i = 0; i < gr[v].size(); i++)
    {
        int to = gr[v][i][0];
        int x = gr[v][i][1];
        int y = gr[v][i][2];
        if (to != last)
        {
            have[to].first = mul(have[v].first, y), have[to].second = mul(have[v].second, x);
            vector<int> x_f = factorize(x);
            vector<int> y_f = factorize(y);
            for (int i = 0; i < y_f.size(); i++)
                now[y_f[i]]--;
            for (int i = 0; i < x_f.size(); i++)
            {
                now[x_f[i]]++;
                degrees[x_f[i]] = max(degrees[x_f[i]], now[x_f[i]]);
            }
            dfs(to, v);
            // return to previous
            for (int i = 0; i < x_f.size(); i++)
                now[x_f[i]]--;
            for (int i = 0; i < y_f.size(); i++)
                now[y_f[i]]++;
        }
    }

}

