#include <cstdlib>
#include <queue>
#include <stack>
#include <iomanip>
#include <fstream>

using namespace std;

#define REP(i, n) for(ll i = 0;i < n;i++)
#define REPR(i, n) for(ll i = n;i >= 0;i--)
#define FOR(i, m, n) for(ll i = m;i < n;i++)
#define FORR(i, m, n) for(ll i = m;i >= n;i--)
#define REPO(i, n) for(ll i = 1;i <= n;i++)
#define ll long long
#define INF (ll)1 << 60
#define MINF (-1 * INF)
#define ALL(n) n.begin(),n.end()
#define MOD 1000000007
#define P pair<ll, ll>


int main() {
	cin.tie(0);
	ios::sync_with_stdio(false);
	ll t;
	cin >> t;
	REP(tt, t) {
		ll n, m, x, y, a1 = 0, a2 = 0;
		bool xused[110000] = {}, yused[110000] = {};
		cin >> n >> m >> x >> y;
		vector<vector<ll>> g(n);
		x--;
		y--;
		REP(i, m) {
			ll a, b;
			cin >> a >> b;
			a--;
			b--;
			g[a].push_back(b);
			g[b].push_back(a);
		}
		queue<ll> q;
		q.push(x);
		while (!q.empty()) {
			ll now = q.front();
			q.pop();
			xused[now] = true;
			if (now == y)continue;
			REP(i, g[now].size()) {
				if (!xused[g[now][i]])q.push(g[now][i]);
			}
		}
		q.push(y);
		while (!q.empty()) {
			ll now = q.front();
			q.pop();
			yused[now] = true;
			if (now == x)continue;
			REP(i, g[now].size()) {
				if (!yused[g[now][i]])q.push(g[now][i]);
			}
		}
		REP(i, n) {
			if (i == x or i == y)continue;
			else if (xused[i] and yused[i])continue;
			else if (xused[i])a1++;
			else if (yused[i])a2++;
		}
		cout << a1 * a2 << endl;
	}
}

