int TakeNumber(FILE* f)
{
    char ch=fgetc(f);
    int chislo=0; 
    while((int(ch)>47)&&(int(ch)<58))
    {
        chislo*=10;
        chislo+=int(ch)-48;
        ch=fgetc(f);
    }
    return chislo;
}

int ToChar(int i,char* buff)
{
    int n=0;
    int j=i;
    while(int(j/=10)) n++;
    for(int k=n;k>=0;k--)
    {
        buff[k]=(i%10)+48;
        i/=10;
    }
    return n+1;
}

