                    b.len++;
                    b.digit[i + a.len] = p;
                }
                res = res + b;
            }
        }
        return res;
    }
    very_long operator / (const very_long &a) const {
        very_long res = 0, c = 0;
        for (int ii = len - 1; ii >= -1; --ii) {
            int l = 0, r = 10;
            while (r - l > 1) {
                int mid = (l + r) / 2;
                if (a * mid <= c) {
                    l = mid;
                } else {
                    r = mid;
                }
            }
            res.digit[res.len] = l;
            ++res.len;
