const ld eps=1e-8;
const int N=1e5+10;

int gi() {
	int x=0,o=1;char ch=getchar();
	while((ch<'0'||ch>'9')&&ch!='-') ch=getchar();
	if(ch=='-') o=-1,ch=getchar();
	while(ch>='0'&&ch<='9') x=x*10+ch-'0',ch=getchar();
	return x*o;
}

bool eq(ld a,ld b) {
	return fabsl(a-b)<eps;
}

struct point {
	ld x,y,z;
	point(ld x=0,ld y=0,ld z=0):x(x),y(y),z(z) {}
	point operator+(const point &A) const {
		return point(x+A.x,y+A.y,z+A.z);
	}
	point operator-(const point &A) const {
		return point(x-A.x,y-A.y,z-A.z);
	}
	point operator*(ld v) const {
		return point(x*v,y*v,z*v);
	}
	point operator/(ld v) const {
		return point(x/v,y/v,z/v);
	}
	ld operator*(const point &A) const {
		return x*A.x+y*A.y+z*A.z;
	}
	point operator%(const point &A) const {
		return point(y*A.z-z*A.y,z*A.x-x*A.z,x*A.y-y*A.x);
	}
	ld len() const {
		return sqrtl(x*x+y*y+z*z);
	}
	void read() {
		x=gi();y=gi();z=gi();
	}
	void print() {
		cerr<<x<<' '<<y<<' '<<z<<'\n';
	}
} p1[N],p2[N];

struct line {
	point st,v;
	line() {}
	line(point st,point v):st(st),v(v) {}
} s;

struct flat {
	point st,nor;
	flat(point a,point b,point c) {
		st=a;nor=(b-a)%(c-a);
	}
};

int n,m;

point sec(line a,line b) {
	ld p=((b.st-a.st)%a.v).len()/(a.v%b.v).len();
	return b.st+b.v*p;
}

ld dist(point a,flat b) {
	return (a-b.st)*b.nor/b.nor.len();
}

point sec(line a,flat b) {
	point p=a.st,q=a.st+a.v;
	ld d1=dist(p,b),d2=dist(q,b);
	return p+(q-p)*d1/(d1-d2);
}

line sec(flat a,flat b) {
	line tmp(a.st,a.nor%point(23333,66666,47777));
	point p=sec(tmp,b);
	return line(p,a.nor%b.nor);
}

bool cmp(point a,point b) {
	return (b-a)*s.v>0;
}

int cal(vector<point> a,vector<point> b) {
	/*for(auto x:a) x.print();
	cerr<<endl<<endl;
	for(auto x:b) x.print();
	cerr<<endl<<endl;*/
	sort(a.begin(),a.end(),cmp);sort(b.begin(),b.end(),cmp);
	int ret=0;
	for(int i=0,j=0;i<sz(a)||j<sz(b);) {
		if(i<sz(a)&&(j==sz(b)||cmp(a[i],b[j]))) ++i;
		else ret+=i&1,++j;
	}
	//cerr<<ret<<' ';
	return ret;
}

