	int pos=1;
	for(int i=1;i<=n;i++) if(!eq(f1.nor*(p2[i]-f1.st),0)) { pos=i;break; }
	rotate(p2+1,p2+pos,p2+n+1);
	//cerr<<pos<<endl;
	pos=1;
	for(int i=1;i<=m;i++) if(!eq(f2.nor*(p1[i]-f2.st),0)) { pos=i;break; }
	rotate(p1+1,p1+pos,p1+m+1);
	s=sec(f1,f2);p1[n+1]=p1[1];p2[m+1]=p2[1];
	//s.st.print();s.v.print();
	ld pre=f1.nor*(p2[1]-f1.st);
	vector<point> vec1,vec2[2];
