			//cerr<<i<<' ';
			//p2[i-1].print();(p2[i]-p2[i-1]).print();
		}
	}
	//for(auto x:vec1) x.print();
	//exit(0);
	pre=f2.nor*(p1[1]-f2.st);int fl=0;
	for(int i=2;i<=n+1;i++) {
		ld now=f2.nor*(p1[i]-f2.st);
		if(pre*now<-eps) pre=now,vec2[fl^=1].pb(sec(s,line(p1[i-1],p1[i]-p1[i-1])));
	}
	//for(auto x:vec2[1]) x.print();
	//exit(0);
	cout<<(cal(vec1,vec2[0])==cal(vec1,vec2[1])?"NO\n":"YES\n");
	return 0;
}

