//package raif;

import java.io.*;
import java.util.*;
import java.util.stream.Collectors;

// What's the smallest input?
// Check * for int/long overflow.
// Check / for accidental rounding.
// Are you using doubles? Can you avoid it?
// Never compare after taking mod.
// Mod the final result.
// Initialize globals in solve() unless they are independent of the problem input.
// Check for local/global name conflicts (n?).
// Check initial values for max/min (not Integer.MIN_VALUE for long).
public class D {
    static Random rand;
    static boolean MULTIPLE_CASES = false;
    static boolean CONSTRUCTIVE = true;
    static long MOD = (long) 1e9 + 7;
    static long BIG = (long) 2e9;

    static class Target {
        int r, c;

        public Target(int r, int c) {
            this.r = r+1;
            this.c = c+1;
        }

        @Override
        public String toString() {
            return r + " " + c;
        }
    }

    public static void solve(Reader in, PrintWriter out) {
        int n = in.nextInt();
        int[] arr = in.nextInts(n);

        Stack<Integer> ones = new Stack<>();
        Stack<Integer> twosAndThrees = new Stack<>();
        for (int i = n - 1; i >= 0; i--) {
            if (arr[i] == 1) {
                ones.add(i);
            } else if (arr[i] == 2 || arr[i] == 3) {
                twosAndThrees.add(i);
            }
        }

        List<Target> ans = new ArrayList<>();

        boolean ok = true;
        for (int i = 0; i < n; i++) {
            if (arr[i] == 0) continue;
            ans.add(new Target(i, i));

            if (arr[i] == 3) {
                // would be nice to find a 2 or 3
                while (!twosAndThrees.isEmpty() && twosAndThrees.peek() <= i) twosAndThrees.pop();

