#include "bits/stdc++.h"
using namespace std;

#define sz(x) (int)(x.size())
#define FOR(i,L,R) for(int i = L; (L <= R? i <= R : i >= R); (L <= R? i += 1 : i -= 1))
#define int long long

const int N = 5e5 + 2, inf = 1e18;
const int BUCKETS = (int)(sqrt(N)) + 2;

int n, q, a[N], lazy[BUCKETS];
map<int,vector<int>> values[BUCKETS];

int bucket_id(int id) {
    return id/BUCKETS;
}

void preprocess() {
    FOR(i,0,n-1) {
        int bId = bucket_id(i);
        values[bId][a[i]].push_back(i);
    }
    FOR(i,0,BUCKETS-1) {
        for(auto &it : values[i]) {
            sort(it.second.begin(), it.second.end());
        }
    }
}

void full_update_bucket(int bId, int L, int R, int add) {
    // auto& mp = values[bId];
    map<int,vector<int>> next;
    for(auto it : values[bId]) {
        int val = it.first;
        for(int pos : it.second) {
            if(pos >= L && pos <= R) {
                next[val + add].push_back(pos);
            }else {
                next[val].push_back(pos);
            }
        }
    }
    values[bId].clear();
    for(auto &it : next) {
        sort(it.second.begin(), it.second.end());
    }
    values[bId] = next;
}

void lazy_update_bucket(int id, int add) {
    lazy[id] += add;
}

void update(int L, int R, int val) {
    int leftmost = bucket_id(L), rightmost = bucket_id(R);

    if(leftmost == rightmost) {
        full_update_bucket(leftmost, L, R, val);
    }else {
        // full_update_bucket(leftmost, L, R, val);
        // FOR(i,leftmost + 1, rightmost - 1) {
        //     lazy_update_bucket(i, val);
        // }
        // full_update_bucket(rightmost, L, R, val);

        FOR(i,leftmost, rightmost) {
            full_update_bucket(i, L, R, val);
        }
    }
}

int query(int val) {
    int L = inf, R = -inf;
    FOR(i,0,BUCKETS-1) {
        int chk = val - lazy[i];
        if(values[i].count(chk)) {
            for(int &pos : values[i][chk]) {    //not optimal
                L = min(L, pos);
                R = max(R, pos);
            }
        }
    }
    if(L == inf) return -1;
    return R - L;
}

int32_t main(){
    ios::sync_with_stdio(false);
    cin.tie(nullptr); cout.tie(nullptr);

    cin >> n >> q;
    FOR(i,0,n-1) {
        cin >> a[i];
    }
    preprocess();
    FOR(i,1,q) {
        int type;   cin >> type;
        if(type == 1) {
            int L, R, add;
            cin >> L >> R >> add;
            --L, --R;
            update(L, R, add);
        }else {
            int val;
            cin >> val;
            cout << query(val) << '\n';
        }
    }
}

