import java.util.*;
import java.io.*;
import java.math.*;
public class Euler {


	public static void main(String[] args){

		FastReader in = new FastReader();
		PrintWriter o = new PrintWriter(System.out);

		int n = in.nextInt();
		long[] ar = new long[n];
		for (int i = 0; i < n; i++) ar[i] = in.nextLong();

		Arrays.sort(ar);

		long ans = 0;
		for (int i = 0; i < n; i++) {
			ans += ar[i] - 1;
		}

		for (int i = 2; i < 1e5; i++) {
			long cr = 0;
			long x = 1;

			for (int j = 0; j < n; j++) {
				cr += Math.abs(x - ar[0]);
				x *= i;
				if (cr > ans) break;
			}
			ans = Math.min(cr, ans);
		}
		o.println(ans);



		o.close();
        o.flush();
        return;

	}


	static class FastReader {
		BufferedReader br;
		StringTokenizer st;

		public FastReader() {
			br = new BufferedReader(new InputStreamReader(System.in));
		}

		String next() {
			while (st == null || !st.hasMoreElements()) {
				try {
					st = new StringTokenizer(br.readLine());
				} catch (IOException e) {
					e.printStackTrace();
				}
			}
			return st.nextToken();
		}

		int nextInt() {
			return Integer.parseInt(next());
		}

		long nextLong() {
			return Long.parseLong(next());
		}

		double nextDouble() {
			return Double.parseDouble(next());
		}

		String nextLine() {
			String str = "";
			try {
				str = br.readLine();
			} catch (IOException e) {
				e.printStackTrace();
			}
			return str;
		}
	}


}

