private void processDiseases() throws java.lang.Exception {
    logger.info("loading disease terms");
    java.lang.String diseaseTermQuery = "select t.term_key, t.term, t.primary_id, a.marker_key, " + (((("  a.genotype_key, a.organism_key, a.hdp_annotation_key " + "from term t ") + "left outer join hdp_annotation a on (t.term_key = a.term_key) ") + "where t.vocab_name = 'OMIM' ") + "  and t.is_obsolete = 0");
    java.sql.ResultSet rs = ex.executeProto(diseaseTermQuery, cursorLimit);
    logger.debug(("  - finished disease query in " + (ex.getTimestamp())));
    java.util.Collection<org.apache.solr.common.SolrInputDocument> docs = new java.util.ArrayList<org.apache.solr.common.SolrInputDocument>();
    while (rs.next()) {
        uniqueKey += 1;
        java.lang.Integer termKey = rs.getInt("term_key");
        java.lang.String term = rs.getString("term");
        java.lang.String termId = rs.getString("primary_id");
        java.lang.Integer markerKey = rs.getInt("marker_key");
        java.lang.Integer organismKey = rs.getInt("organism_key");
        java.lang.Integer annotationKey = rs.getInt("hdp_annotation_key");
        org.jax.mgi.shr.DistinctSolrInputDocument doc = new org.jax.mgi.shr.DistinctSolrInputDocument();
        doc.addField(DiseasePortalFields.UNIQUE_KEY, uniqueKey);
        doc.addField(DiseasePortalFields.TERM, term);
        doc.addField(DiseasePortalFields.TERM_ID, termId);
        doc.addField(DiseasePortalFields.TERM_TYPE, omim);
        org.jax.mgi.indexer.BSU bsu = null;
        if (organismKey == 1) {
            bsu = this.getMouseBsu(getGenocluster(rs.getInt("genotype_key")), getGridClusterKey(markerKey));
        }else
            if (organismKey == 2) {
                bsu = getHumanBsu(markerKey, termKey);
            }

        if (bsu != null) {
            doc.addField(DiseasePortalFields.GRID_KEY, bsu.bsuKey);
        }
        if ((markerKey == null) || (isHuman(markerKey))) {
            addHpoData(doc, termKey);
        }
        int termSort = getTermSequenceNum(term);
        doc.addField(DiseasePortalFields.BY_TERM_NAME, termSort);
        addAllFromLookup(doc, DiseasePortalFields.TERM_SYNONYM, termId, termSynonymMap);
        addAll(doc, DiseasePortalFields.TERM_ALT_ID, getAlternateTermIds(termId));
        if (headersPerTerm.containsKey(termId)) {
            addAllFromLookup(doc, DiseasePortalFields.TERM_HEADER, termId, headersPerTerm);
        }else {
            doc.addField(DiseasePortalFields.TERM_HEADER, term);
        }
        doc.addField(DiseasePortalFields.DISEASE_REF_COUNT, getDiseaseReferenceCount(termId));
        doc.addField(DiseasePortalFields.DISEASE_MODEL_COUNTS, getDiseaseModelCount(termId));
        addAll(doc, DiseasePortalFields.MP_TERM_FOR_DISEASE_TEXT, getRelatedPhenotypes(annotationKey, true, false));
        addAll(doc, DiseasePortalFields.MP_TERM_FOR_DISEASE_ID, getRelatedPhenotypes(annotationKey, false, true));
        addAll(doc, DiseasePortalFields.OMIM_TERM_FOR_DISEASE_TEXT, getRelatedDiseases(annotationKey, true, false));
        addAll(doc, DiseasePortalFields.OMIM_TERM_FOR_DISEASE_ID, getRelatedDiseases(annotationKey, false, true));
        if (markerKey != null) {
            java.util.Set<java.lang.String> featureTypes = new java.util.HashSet<java.lang.String>();
            java.util.Set<java.lang.String> markerSynonyms = new java.util.HashSet<java.lang.String>();
            java.util.Set<java.lang.String> orthologNomen = new java.util.HashSet<java.lang.String>();
            java.util.Set<java.lang.String> orthologSymbols = new java.util.HashSet<java.lang.String>();
            java.util.Set<java.lang.String> orthologSynonyms = new java.util.HashSet<java.lang.String>();
            java.util.Set<java.lang.String> orthologIds = new java.util.HashSet<java.lang.String>();
            java.lang.String markerSymbol = getMarkerSymbol(markerKey);
            java.lang.Integer gridClusterKey = getGridClusterKey(markerKey);
            doc.addField(DiseasePortalFields.MARKER_KEY, markerKey);
            addIfNotNull(doc, DiseasePortalFields.MARKER_SYMBOL, markerSymbol);
            addIfNotNull(doc, DiseasePortalFields.MARKER_NAME, getMarkerName(markerKey));
            addIfNotNull(doc, DiseasePortalFields.MARKER_MGI_ID, getMarkerID(markerKey));
            if (markerSynonymMap.containsKey(markerKey.toString())) {
                markerSynonyms.addAll(markerSynonymMap.get(markerKey.toString()));
            }
            addAll(doc, DiseasePortalFields.MARKER_ID, getMarkerIds(markerKey));
            if (gridClusterKey != null) {
                java.lang.String gckString = gridClusterKey.toString();
                doc.addField(DiseasePortalFields.GRID_CLUSTER_KEY, gckString);
                if (featureTypeMap.containsKey(gckString)) {
                    featureTypes.addAll(featureTypeMap.get(gckString));
                }
            }else {
                java.util.Set<java.lang.String> mFeatureTypes = getMarkerFeatureTypes(markerKey);
                if (mFeatureTypes != null) {
                    featureTypes.addAll(mFeatureTypes);
                }
            }
            this.addMarkerCoordinates(doc, markerKey, true);
            java.util.Set<java.lang.String> markerKeys = getMarkersByDisease(termId);
            if (markerKeys != null) {
                for (java.lang.String stringMarkerKey : markerKeys) {
                    java.lang.Integer mkey = java.lang.Integer.parseInt(stringMarkerKey);
                    java.lang.String msymbol = getMarkerSymbol(mkey);
                    if (isHuman(mkey)) {
                        doc.addField(DiseasePortalFields.TERM_HUMANSYMBOL, msymbol);
                    }else {
                        doc.addField(DiseasePortalFields.TERM_MOUSESYMBOL, msymbol);
                    }
                }
            }
            java.util.Set<java.lang.Integer> orthologousMarkerKeys = getMarkerOrthologs(markerKey);
            if (orthologousMarkerKeys != null) {
                for (java.lang.Integer orthoMarkerKey : orthologousMarkerKeys) {
                    java.lang.String orthoSymbol = getMarkerSymbol(orthoMarkerKey);
                    java.lang.String orthoName = getMarkerName(orthoMarkerKey);
                    java.util.Set<java.lang.String> orthoIds = getMarkerIds(orthoMarkerKey);
                    java.util.Set<java.lang.String> orthoSynonyms = getMarkerSynonyms(orthoMarkerKey);
                    if (orthoSymbol != null) {
                        orthologSymbols.add(orthoSymbol);
                    }
                    if (orthoName != null) {
                        orthologNomen.add(orthoName);
                    }
                    if (orthoSynonyms != null) {
                        orthologSynonyms.addAll(orthoSynonyms);
                    }
                    if (orthoIds != null) {
                        orthologIds.addAll(orthoIds);
                    }
                }
            }
            if ((featureTypes.size()) > 0) {
                addAll(doc, DiseasePortalFields.FILTERABLE_FEATURE_TYPES, featureTypes);
            }
            if ((markerSynonyms.size()) > 0) {
                addAll(doc, DiseasePortalFields.MARKER_SYNONYM, markerSynonyms);
                addAll(doc, DiseasePortalFields.MARKER_SYNONYM_SINGLE_TOKEN, getMarkerSingleTokenSynonyms(markerKey));
            }
            if ((orthologNomen.size()) > 0) {
                addAll(doc, DiseasePortalFields.ORTHOLOG_NOMEN, orthologNomen);
            }
            if ((orthologSymbols.size()) > 0) {
                addAll(doc, DiseasePortalFields.ORTHOLOG_SYMBOL, orthologSymbols);
            }
            if ((orthologSynonyms.size()) > 0) {
                addAll(doc, DiseasePortalFields.ORTHOLOG_SYNONYM, orthologSynonyms);
            }
            if ((orthologIds.size()) > 0) {
                addAll(doc, DiseasePortalFields.ORTHOLOG_ID, orthologIds);
            }
            addExpressedComponents(doc, markerKey);
        }
        docs.add(doc);
        if ((docs.size()) >= (solrBatchSize)) {
            writeDocs(docs);
            docs = new java.util.ArrayList<org.apache.solr.common.SolrInputDocument>();
        }
    } 
    writeDocs(docs);
    commit();
    rs.close();
    logger.info((("done processing " + (uniqueKey)) + " disease terms"));
}

