int fenwick_sum(std::vector<int> B, int i){
	int sum {};
	while(i != 0){
		sum += B[i];
		i -= i & -i;
	}
	return sum;
}

void fenwick_update(std::vector<int> &B, int i, int v){
	while(i < B.size()){
		B[i] += v;
		i += i & -i;
	}
}


int main() {
