	map.reserve(n);
	for (int i {}; i < n; ++i) {
		scanf("%d", &x);
		vec.push_back(x);
	}

	//create prefix counter array
	//P[i] = f(1, i, a_i)

	std::vector<int> P;
	for(auto it = vec.begin(); it <vec.end(); ++it){
		P.push_back(++map[*it]);
	}
	map.clear();

	//create suffix counter array
	//S[j] = f(j, n, a_j)
	std::vector<int> S(n);
	for(auto it = vec.rbegin(); it <vec.rend(); ++it)
		S.push_back(++map[*it]);

	//scans P backward, for each possible i
