const llong inf = (llong) 1e+9 + 7ll;
const llong linf = (llong) 1e+18 + 7ll;
const long double eps = (long double) 1e-9;
const long double pi = acosl((long double) -1.0);
const int alph = 26;

mt19937 mrand(random_device{} ()); 

template<typename T, typename U> inline llong umin(const T& a, const U& b) { return a < b ? a : b; }
template<typename T, typename U> inline llong umax(const T& a, const U& b) { return a > b ? a : b; }

static char buff[(int) 2e6 + 17]; // reads std::string
const int maxn = (int) 2e2 + 17;

int n;
llong k;
llong a[maxn];

bool read()
{
	if (scanf("%d %lld", &n, &k) != 2)
		return false;

	forn (i, n)
		scanf("%lld", a + i);

	return true;
}

llong z(llong a, llong b) { return (a + b - 1) / b; }

llong get(llong x)
{
	llong res = 0;

	forn (i, n)
		res += z(a[i], x);

	return res;
}

void solve()
{
	set<llong> ls;

	forn (i, n)
	{
		vector<llong> t;

		for (llong j = 1; (j - 1) * (j - 1) <= a[i]; ++j)
			t.push_back(z(a[i], j));

		for (llong j = 1; j * j <= a[i]; ++j)
			t.push_back(j);

		sort(t.begin(), t.end());
		t.resize(unique(t.begin(), t.end()) - t.begin());

		forn (i, size(t))
			ls.insert(t[i]);
	}

	llong ans = 1, sm = k + accumulate(a, a + n, 0ll);

	for (auto p = ls.begin(); p != ls.end(); ++p)
	{
		llong x = *p;
		llong rr = get(x);
		llong lq = x;
		llong rq = (x == *ls.rbegin() ? z(sm, rr) : *ls.upper_bound(x));
		llong l = lq, r = rq;

		for (; l < r; )
		{
			llong m = (l + r + 1) / 2;

			if (m * rr <= sm)
				l = m;
			else
				r = m - 1;
		}

		if (l * rr <= sm)
			ans = max(ans, l);
	}

	printf("%lld\n", ans);
}

int main()
{
#if SEREZHKA
	freopen("file.in", "r", stdin);
#endif

	while (read())
		solve();

	return 0;
}

