#include <iostream>
#include <stdio.h>
#include <stdlib.h>
#include <algorithm>
#include <cstring>
#include <cmath>
using namespace std ;
const int maxn = 520 ;
const int mmax = 250005 ;
struct Node
{
    int sc , xx ; 
};
Node A[maxn] ;
int nowcnt = 0 , a[mmax] , ans[maxn] , ccnt = 0 ;

void ins(int val)
{
    for (int i = 0 ; i < nowcnt ; ++ i) {
        if (A[i].xx == val) {
            A[i].sc ++ ;
            return ;
        }
    }
    A[nowcnt].xx = val ;
    A[nowcnt++].sc = 1 ;
}

int fi(int val)
{
    for (int i =  0 ; i < nowcnt ; ++ i) {
        if (A[i].xx == val) return i ;
    }
    return 0 ;
}

bool cmp(const int &x_ , const int &y_)
{
    if (x_ < y_) return false ;
    return true ;
}

int gcd(int x_,int y_)
{
    return (y_ == 0 ? x_ : gcd(y_,x_%y_)) ;
}

void sol(int val)
{
    int gg , id ;
    for (int i = 0 ; i < ccnt ; ++ i) {
        gg = gcd(val,ans[i]) ;
        id = fi(gg) ;
        A[id].sc -= 2 ;
    }
}

int main()
{
    int n , N ;
    cin >> n ;
    N = n * n ;
    for (int i =  0 ; i < N ; ++ i) {
        scanf("%d",&a[i]) ;
        ins(a[i]) ;
    }
    sort(a,a+N,cmp) ;
    ans[ccnt++] = a[0] ;
    int id , tmpnum ;
    id = fi(a[0]) ;
    A[id].sc -- ;
    for (int i = 1 ; i < N ; ++ i) {
        id = fi(a[i]) ;
         tmpnum = A[id].sc ;
        if (tmpnum <= 0) continue ;
        else {
            sol(a[i]) ;
            ans[ccnt++] = a[i] ;
            id = fi(a[i]) ;
            A[id].sc -- ;
        }
    }
    for (int i = 0 ; i < ccnt ; ++ i) {
        printf("%d ", ans[i]);
    }
    puts("") ;
    return 0 ;
}

