#include<bits/stdc++.h>
using namespace std;
int n, m, k, cnt;
const int N = 300003,INF = 0x3f3f3f3f;
int root[N], seg[N << 5],ls[N << 5],rs[N << 5];
struct Seg {
    int l,r,p;
    inline bool operator < (const Seg &o) const {return l < o.l || l == o.l && r < o.r;}
} a[N];
inline void change(int &nx, int ox, int L, int R, int pos, int val){
    seg[nx = ++ cnt] = seg[ox];
    ls[nx] = ls[ox]; 
	rs[nx] = rs[ox];
    if(L == R){
        seg[nx] = min(seg[nx], val);
        return;
    }
    int mid = L + R >> 1;
    if(pos<=mid) {
    	change(ls[nx], ls[ox], L, mid, pos, val);
	}
    else{
    	change(rs[nx], rs[ox], mid + 1, R, pos, val);
	} 
    pushup(nx);
}
inline void build(int &x, int L, int R){
    seg[x = ++ cnt] = INF;
    if(L == R) return;
    int mid = L + R >> 1;
    build(ls[x], L, mid);
    build(rs[x], mid + 1, R);
}
inline void pushup(int x){
    seg[x] = max(seg[ls[x]], seg[rs[x]]);
}
inline int query(int a, int L, int R, int l, int r){
    if(l <= L && R <= r){
    	return seg[a];
	}
    int mid = L + R >> 1, ans = 0;
    if(l <= mid){
    	ans = max(ans, query(ls[a], L, mid, l, r));
	} 
    if(mid < r){
    	ans = max(ans, query(rs[a], mid + 1, R, l, r));
	} 
    return ans;
}
	//freopen("10.in", "r", stdin);   // 
//	freopen("10.out", "w", stdout);  // 
int main(){
    scanf("%d%d%d", &n, &m, &k);
    for(int i = 1;i <= k;i ++)
        scanf("%d%d%d", &a[i].l, &a[i].r, &a[i].p);
    sort(a + 1, a + k + 1);
    build(root[k + 1], 1, n);
    for(int i = k;i;i --)
        change(root[i], root[i + 1], 1, n, a[i].p, a[i].r);
    a[k + 1].l = INF; 
    while(m --){
        int A, B, X, Y;
        scanf("%d%d%d%d", &A, &B, &X, &Y);
        int ans = -1, mid, left = 1, right = k + 1;
        while(left <= right){
            mid = left + right >> 1;
            if(a[mid].l >= X) ans = mid, right = mid - 1;
            else left = mid + 1;
        }
        puts(query(root[ans], 1, n, A, B) <= Y ? "yes" : "no");
        fflush(stdout);
    }
    //fclose(stdin);   //
	//fclose(stdout);	  //
}

