FILE *stream;
ll n, m, b, Mod, a[505], dp[2][505][505];
int main() {
	ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);
	//freopen_s(&stream, "input.txt", "r", stdin);
	//freopen_s(&stream, "output.txt", "w", stdout);

	cin >> n >> m >> b >> Mod;
	for (int i = 0; i < n; i++)
		cin >> a[i];
	for (int i = n - 1; i >= 0; i--) {
		for (int k = b; k >= 0; k--) {
		    for (int j = m; j >= 0; j--) {
				if (j == m)
					dp[i & 1][j][k] = 1;
				else if (i == n - 1) {
					if (k + a[i] * (m - j) <= b)dp[i & 1][j][k] = 1;
					else dp[i & 1][j][k] = 0;
				}
				else {
					ll c1 = 0, c2 = 0;
					if (k + a[i] <= b)c1 = dp[i & 1][j + 1][k + a[i]];
					c2 = dp[(i + 1) & 1][j][k];
					dp[i & 1][j][k] = (c1 + c2) % Mod;
				}
			}
		}
	}
	cout << dp[0][0][0];

	return 0;
}

