void Sieve()
{
    int n = MAX;
    int nNew = sqrt(n);

    int marked[n / 2 + 500] = {0};

    for (int i = 1; i <= (nNew - 1) / 2; i++)
        for (int j = (i * (i + 1)) << 1; j <= n / 2; j = j + 2 * i + 1)
            marked[j] = 1;

    primes.push_back(2);

