#include <bits/stdc++.h>

using namespace std;

#define ll long long
#define ld long double
#define pll pair <ll , ll>

#define pb push_back
#define pf push_front
#define pob pop_back
#define pof pop_front
#define mp make_pair

#define X first
#define Y second

#define LB(x) ((x) & -(x))
#define BIT(a , b) (((a)>>(b)) & 1)

const ll MAXN=1e5+10;
const ll MAX_LOG=20;
const ll INF=1e10+10;
const ll Mod=1e9+7;

ll w[MAXN][MAX_LOG];
ll dp[MAXN][MAX_LOG];

ll a[MAXN];
ll ps[MAXN];
ll pw2[MAXN];

int main()
{
	ios_base :: sync_with_stdio(false);
	cin.tie(0);

	ll n , q;
	cin>>n>>q;

	for(ll i=1;i<=n;i++)
    {
        cin>>a[i];
    }

    for(ll i=n;i>=1;i--)
    {
        ps[i]=ps[i+1]*2+(Mod+a[i]);
        ps[i]%=Mod;
    }

	pw2[0]=1;

	for(ll i=1;i<MAXN;i++)
    {
        pw2[i]=pw2[i-1]*2;
        pw2[i]%=Mod;
    }

	for(ll i=1;i<=n;i++)
    {
        if (a[i]<0)
        {
            dp[i][0]=i-1;
        }

        if (a[i]==0)
        {
            dp[i][0]=dp[i-1][0];
        }

        if (a[i]>0)
        {
            dp[i][0]=i-1;
            ll x=a[i];

            while (dp[i][0]>0)
            {
                if (x>INF)
                {
                    dp[i][0]=0;
                    break;
                }

                if (2*x>=-a[dp[i][0]])
                {
                    x=2*x+a[dp[i][0]];
                    dp[i][0]--;
                }
                else
                {
                    dp[i][0]--;
                    break;
                }
            }
        }

        w[i][0]=ps[dp[i][0]+1]-((pw2[i-dp[i][0]]*ps[i+1])%Mod)+Mod;
        w[i][0]%=Mod;
    }

    for(ll i=1;i<MAX_LOG;i++)
    {
        for(ll j=1;j<=n;j++)
        {
            dp[j][i]=dp[dp[j][i-1]][i-1];
            w[j][i]=w[j][i-1]+w[dp[j][i-1]][i-1];
            w[j][i]%=Mod;
        }
    }

    for(ll i=1;i<=q;i++)
    {
        ll l , r;
        cin>>l>>r;

        ll res=0;

        for(ll j=MAX_LOG;j>=0;j--)
        {
            if (dp[r][j]>=l)
            {
                res+=w[r][j];
                res%=Mod;
                r=dp[r][j];
            }
        }

        res*=2;
        res%=Mod;

        res+=ps[l]-((pw2[r-l+1]*ps[r+1])%Mod)+Mod;
        res%=Mod;

        cout<<res<<endl;
    }
}

