using namespace std;

int main() {
#ifdef _DEBUG	
	FILE *stream;
	freopen_s(&stream, "input.txt", "r", stdin);
	freopen_s(&stream, "output.txt", "w", stdout);
#endif
	int n;
	cin >> n;

	vector<int> a(n);
	for (size_t i = 0; i < n; ++i)
		cin >> a[i];

	int l = 0;
	int r = n - 1;
	int last = 0;
	string result = "";

	while (l <= r) {
		if (last < a[l] && last < a[r]) {
			if (a[l] < a[r]) {
				result += 'L';
				last = a[l];
				++l;
			}
			else if (a[l] > a[r]) {
				result += 'R';
				last = a[r];
				--r;
			}
			else break;
		}
		else {
			if (a[l] > last) {
				result += 'L';
				last = a[l];
				++l;
				continue;
			}
			if (a[r] > last) {
				result += 'R';
				last = a[r];
				--r;
				continue;
			}
		} 
	}

	last = a[l];
	++l;
	int left_length = 0;
	while (a[l] > last) {
		last = a[l];
		++left_length;
		++l;
	}

	last = a[r];
	--r;
	int right_length = 0;
	while (a[r] > last) {
		last = a[r];
		++right_length;
		--r;
	}
	result += right_length > left_length
		? string(right_length + 1, 'R')
		: string(left_length + 1, 'L');

	cout << result.size() << endl << result;

	return 0;
}

