/**
 * @author atulanand
 */
public class Solution {

  private static int total = 0;

  private static int solve(int[] parent, char[] color) {
    total = 0;
    Map<Integer, List<Integer>> graph = new HashMap<>();
    for (int i = 0; i < parent.length; i++) {
      int curr = (i + 2);
      int par = parent[i];
      List<Integer> children = graph.getOrDefault(par, new ArrayList<>());
      children.add(curr);
      graph.put(par, children);
    }
    int[] res = rec(graph, color, 1);
    return total;
  }
  
  private static int[] rec(Map<Integer, List<Integer>> graph, char[] color, int root) {
    if (!graph.containsKey(root)) {
      int[] res = new int[2];
      if (color[root - 1] == 'W') {
        res[0] = 1;
      }
      if (color[root - 1] == 'B') {
        res[1] = 1;
      }
      return res;
    }
    List<Integer> children = graph.get(root);
    int[] left = new int[2];
    left = rec(graph, color, children.get(0));
    int[] right = new int[2];
    if (children.size() > 1) {
      right = rec(graph, color, children.get(1));
    }
    int W = left[0] + right[0] + (color[root - 1] == 'W' ? 1 : 0);
    int B = left[1] + right[1] + (color[root - 1] == 'B' ? 1 : 0);
    if (W == B) {
      total++;
    }
    return new int[]{W, B};
  }
  
  public static void main(String[] args) throws IOException {
    BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
    int t = inputInt(br);
    StringBuilder sb = new StringBuilder();
