using namespace std;
typedef pair<int, int> pii;
int n,m,used[222];
LL d[222][222];
LL calc()
{
	memset(d,0,sizeof(d));
	d[0][0] = 1;
	for(int i = 1; i <= n + m - 1; i++)
		for(int j = 0; j <= n + m - 1; j++)
			if (used[i - 1])
			{
				if (j - used[i - 1] >= 0) d[i][j] = d[i - 1][j - used[i - 1]];
			} else
			{
				if (j - 1 >= 0) d[i][j] = d[i - 1][j - 1];
				d[i][j] += d[i - 1][j + 1];
			}
	return d[n + m - 1][0];
}
