		}
	for(int i = 0; i < n * m; i++)
	{
		int x = p[i].first;
		int y = p[i].second;
		if (used[x + y]) continue;
		used[x + y] = 1;
		LL val = calc();
		if (val < k)
		{
			used[x + y] = -1;
			k -= val;
		}
	}
	for(int i = 0; i < n; i++)
	{
		for(int j = 0; j < m; j++)
