	int x=0,y=0;
	int maxx=0,minx=0;
	int maxy=0,miny=0;
	//0,0 
	for(int i=0;i<s.size();i++){
		if(s[i]=='L') y--;
		else if(s[i]=='R') y++;
		else if(s[i]=='U') x--;
		else if(s[i]=='D') x++;
		maxx=max(maxx,x);
		maxy=max(maxy,y);
		minx=min(minx,x);
		miny=min(miny,y);
	}
	maxx-=minx;
	maxy-=miny;
	node res={maxx,maxy,-minx,-miny};
	return res;
}
 
