					for(int itr = pos-1;itr>=0;--itr){
						a.push_front(arr[itr]);
					}
				}
				else{
					int mcx=0;
					int cnt=1;
					bool found = false;
					for(int itr=1;itr<a.size();++itr){
						if(a.at(itr)==a.at(itr-1))cnt++;
						else {
							if(cnt>=m){
								found = true;
								break;
							}
							else{
								cnt=1;
								mcx=itr;
							}
						}
					}
					if(cnt>=m){
						found = true;
					}
					if(found){
						int arr[mcx];
						for(int itr = 0;itr<mcx;++itr){
							arr[itr]=a.front();a.pop_front();
						}
						int ans = a.front()*m;
						for(int j=0;j<m;j++)a.pop_front();
						a.push_front(ans);
						for(int itr = mcx-1;itr>=0;--itr){
							a.push_front(arr[itr]);
						}
					}
					else{
						flag=false;
					}
				}
			}
		}
	}
	if(flag==false)sayNo();
	else if(a.size()==0&&b.size()==0)sayYes();
	else sayNo();

}

