		int k = nextInt();
		Set<Long> set = new TreeSet<>();
		for (int i = 1; i <= m; i++) {
			int x = nextInt();
			int y = nextInt();
			if (x==y)
				continue;
			long hash1 = (long)(n+1) * x + y;
			long hash2 = (long)(n+1) * y + x;
			if (set.contains(hash1) || set.contains(hash2))
				continue;
			ages[x].add(y);
			ages[y].add(x);
