#include<bits/stdc++.h>
#define rep(i,a,n) for(int i=a;i<=n;i++)
#define per(i,a,n) for(int i=n;i>=a;i--)
#define pb push_back
#define mp make_pair
#define FI first
#define SE second
#define maxn 300000
#define mod 1000000007
#define inf 0x3f3f3f3f
using namespace std;
typedef long long ll;
typedef pair<int,int> pii;
typedef vector<int> vi;
typedef double db;

set<pii> S;
vector<pii> upd[maxn+5];
int n;

void ins(int id,int from,int len)
{
    upd[id].pb(mp(from,len));
}

ll pre[maxn+5];
ll sum;
ll cal(int x)
{
    int now=1;
    ll cnt=0;
    ll s=0; sum=0;
    memset(pre,0,sizeof(ll)*(n+2));
    rep(i,1,n)
    {
        for(auto it: upd[i])
        {
            int from=it.FI;
            int len=it.SE;
            if(from+1<now)
            {
                s+=1ll*(now-from-1)*len;
                pre[now]+=len;
            }
            else pre[from+1]+=len;
            pre[i+1]-=len;
        }
        while(now<=n && pre[now]>=x) s+=pre[now++],pre[now]+=pre[now-1];
        sum+=s;
        cnt+=now-1;
    }
    return cnt;
}

int main()
{
    int k; scanf("%d%d",&n,&k);
    S.insert(mp(inf,0));
    rep(i,1,n)
    {
        int l,r; scanf("%d%d",&l,&r);
        int L=l,oldid=-1;
        while(1)
        {
            auto it=S.lower_bound(mp(L,0));
            if(oldid==-1) oldid=it->SE;
            if(it->FI<=r)
            {
                ins(i,it->SE,it->FI-L);
                L=it->FI;
                S.erase(it);
            }
            else
            {
                ins(i,it->SE,r-L);
                S.insert(mp(l,oldid));
                S.insert(mp(r,i));
                break;
            }
        }
    }
    int l=1,r=inf;
    while(l<r)
    {
        int mid=(l+r)>>1;
        int cnt=cal(mid);
        if(cnt<k) r=mid;
        else l=mid+1;
    }
    int cnt=cal(l);
    ll ans=sum+1ll*(k-cnt)*(l-1);
    printf("%I64d\n",ans);
    return 0;
}

