        for (nodo_arbol;nodo_arbol>0;nodo_arbol>>=1) {
            st.arr[nodo_arbol>>1] = make_pair(
                max(st.arr[nodo_arbol].fi, st.arr[nodo_arbol^1].fi),
                max(st.arr[nodo_arbol].se, st.arr[nodo_arbol^1].se)
            );
        }
