{
	if(st.empty())	return false;
	type ite = --st.end();
	if(*ite>a && *ite>b && *ite>c)	return false;
	int val = *ite;
	st.erase(ite);
	if(a>=val)
		handle(b,c);
	else if(b>=val)
		handle(a,c);
	else
		handle(a,b);
	return true;
}

