	if(*maxi>a+b+c)
	{
		cout<<-1;
		return 0;
	}
	s[0]=node(a+b,c);
	s[1]=node(a+c,b);
	s[2]=node(b+c,a);
	sort(s,s+3);
	int ans=0;
	while(!st.empty())
	{
		type ite = --st.end();
