#include <iostream>
#include <cstdio>
#include <cstring>
using namespace std;
typedef long long ll;
const int N=200;
const int mod=998244353;
int primes[N],tot;
int p[20000];
int inv[20000];
int cnt[100000];
ll gcd(ll a,ll b){
    return b==0?a:gcd(b,a%b);
}
int C(int n,int m)
{
    if(m>n)return 0;
    return ((ll)p[n]*inv[m]%mod*inv[n-m])%mod;
}
int qmi(int a,int b=mod-2)
{
    int ans=1;
    while(b){
        if(b&1)ans=(ll)ans*a%mod;
        a=(ll)a*a%mod;
        b>>=1;
    }return ans;
}
int get_ans(ll k)
{
    tot=0;
    for(ll i=2;i<=k/i;i++)
    {
        if(k%i==0){
            int num=0;
            while(k%i==0){
                num++;
                k/=i;
            }
            cnt[++tot]=num;
        }
    }
    if(k>1){
        cnt[++tot]=1;
    }
    int sum=0;
    for(int i=1;i<=tot;i++)sum+=cnt[i];
    ll ans=1;
    for(int i=1;i<=tot;i++)
    {
        ans=(ans*C(sum,cnt[i]))%mod;
        sum-=cnt[i];
    }
    return ans;
}
int main()
{
    ll d;
    cin>>d;
    int T;
    cin>>T;
    inv[0]=1;
    p[0]=1;
    for(int i=1;i<=199;i++)
    {
        p[i]=(ll)p[i-1]*i%mod;
        inv[i]=(ll)inv[i-1]*qmi(i)%mod;
    }
    while(T--)
    {
        ll u,v;
        scanf("%lld%lld",&u,&v);
        if(u>v)swap(u,v);
        ll t=gcd(u,v);
        ll  ans=(ll)get_ans(u/t)*get_ans(v/t)%mod;
        printf("%lld\n",ans);
    }
}

