	ldb used=0,x=time*p;
	for(int i=1;i<=n;++i)if(a[i]*time>b[i]){
		used+=(a[i]*time-b[i]);
		if(used>x)return false;
	}
	return true;
}
int main(){
	scanf("%d%d",&n,&x),p=x;
	for(int i=1;i<=n;++i)
		scanf("%d",&x),a[i]=x,
		scanf("%d",&x),b[i]=x;
	ldb l=0,r=1e20;
	while(r-l>1e-6){
		ldb mid=(l+r)/2;
		if(check(mid))l=mid;
		else r=mid;
	}
	if(l>1e19)printf("-1");
	else printf("%.9lf",(double)l);
}

