        }

        dp[x]++;
    }

    for (ll i = 0; i < 24; i++) {
        for (ll mask = 0; mask < (1 << 24); mask++) {
            if (mask & (1 << i)) dp[mask] += dp[mask ^ (1 << i)];
        }
    }

    ll ans = 0;
    for (ll mask = 0; mask < (1 << 24); mask++) {
        ans ^= sqr(n - dp[mask]);
    }

    cout << ans;

