const int N = 2e5+7;
int n;
map<string, int> dic;
set<int> act;
int par[N], rnk[N];
int op[N];

int find(int x)
{
    if (x == 0) return 0;

    if (par[x] == 0) {
        par[x] = x;
        rnk[x] = 0;
        return x;
    }

    if (par[x] != x)
        par[x] = find(par[x]);
    return par[x];
}

void unite(int x, int y)
{
    if (x == 0 || y == 0) return;

    int xr = find(x);
    int yr = find(y);
    if (xr == yr) return;
    if (rnk[xr] < rnk[yr])
        par[xr] = yr;
    else if (rnk[xr] > rnk[yr])
        par[yr] = xr;
    else {
        par[yr] = xr;
        rnk[xr]++;
    }
}

void solve()
{
    int m, q;
    cin >> n >> m >> q;
    for (int i = 1; i <= n; i++) {
        string w; 
        cin >> w;
        dic[w] = i;
    }

    for (int k = 0; k < m; k++) {
        int type;
        string as, bs;
        cin >> type >> as >> bs;
        int a = dic[as];
        int b = dic[bs];

        bool flag = true;
        int ar = find(a);
        int br = find(b);
        if (type == 1) {
            if (ar == op[br]) {
                flag = false;
            }
            else {
                unite(ar, br);
                unite(op[ar], op[br]);
                int new_ar = find(ar);
                int new_op = find(op[ar]);
                op[new_ar] = new_op;
                op[new_op] = new_ar;
            }
        }
        else {
            if (ar == br || (ar != br && op[ar] && op[br])) {
                flag = false;
            }
            else {
                unite(op[ar], br);
                unite(op[br], ar);
                op[ar] = find(br);
                op[br] = find(ar);
            }
        }
        if (flag) cout << "YES" << endl;
        else cout << "NO" << endl;
    }

    for (int k = 0; k < q; k++) {
        string as, bs;
        cin >> as >> bs;
        int a = dic[as];
        int b = dic[bs];
        int ar = find(a);
        int br = find(b);
        if (ar == br) cout << "1" << endl;
        else if (ar == op[br]) cout << "2" << endl;
        else cout << "3" << endl;
    }
}

