#include <iostream>
#include <vector>
#include <queue>

using namespace std;

typedef pair<int, int> ii;

struct node {
    int val = 0, lp = 0;
};

vector<vector<int> > vec(1000010, vector<int> () );
vector<vector<ii> > tree(1000010, vector<ii> () );
int a, b, val[1000010], level[1000010];
node all[3000010];

void updateLp(int _at) {
    all[_at].val += all[_at].lp;
    all[_at*2].lp += all[_at].lp;
    all[_at*2+1].lp += all[_at].lp;
    all[_at].lp = 0;
}

void updateRange(int l, int r, int x, int _at = 1, int curL = 0, int curR = (1<<20)-1) {
    updateLp(_at);
    if (curL >= l && curR <= r) {all[_at].lp += x;}
    else if (curL > r || l > curR) {}
    else {
        updateRange(l, r, x, _at*2, curL, curL+(curR-curL)/2 );
        updateRange(l, r, x, _at*2+1, curL+(curR-curL)/2+1, curR );
    }
}

int getElemAtPos(int pos, int _at = 1, int curL = 0, int curR = (1<<20)-1) {
    updateLp(_at);
    if (curL >= pos && curR <= pos) {return all[_at].val;}
    else if (curL > pos || pos > curR) {return 0;}
    else {
        int r1 = getElemAtPos(pos, _at*2, curL, curL+(curR-curL)/2 );
        int r2 = getElemAtPos(pos, _at*2+1, curL+(curR-curL)/2+1, curR );
        return r1 + r2;
    }
}

void DFS(int node, int parent, int _add, int tm) {
    for (int i = 0; i < tree[node].size(); ++i ) {
        int lenght = tree[node][i].first;
        int addby = tree[node][i].second;
        updateRange(tm, tm+lenght, addby);
    }

    val[node] += getElemAtPos(tm);

    for (int i = 0; i < vec[node].size(); ++i) {
        int ngb = vec[node][i];
        if (ngb == parent) continue;

        DFS(ngb, node, _add, tm+1);
    }

    for (int i = 0; i < tree[node].size(); ++i ) {
        int lenght = tree[node][i].first;
        int addby = tree[node][i].second;
        updateRange(tm, tm+lenght, -addby);
    }

}

