        vec[temp1].push_back(temp2);
        vec[temp2].push_back(temp1);
    }
    cin >> b;
    for (int i = 0; i < b; ++i) {
        int temp1, temp2, temp3;
        cin >> temp1 >> temp2 >> temp3;
        tree[temp1].push_back( ii(temp2, temp3) );
    }

    DFS(1, -1, 0, 1);

    for (int i = 1; i <= a; ++i) cout << val[i] << " ";
    cout << endl;

    return 0;
}

