	tree[k].l = l;
	tree[k].r = r;
	if(l == r){
		tree[k].sum = a[l];
		tree[k].mx = a[l];
		return;
	}
	int mid = (l + r) / 2;
	build(k * 2,l,mid);
	build(k * 2 + 1,mid + 1,r);
	pushUp(k);
}

