lli max(lli a,lli b)
{
   if(a>b)
   {
       return a;
   }
   return b;
}
lli gcd(lli a,lli b)
{
    if(a==0)
    {
        return b;
    }
    return gcd(b%a,a);
}
lli min(lli a,lli b)
{
   if(a<b)
   {
       return a;
   }
   return b;
}
lli power(lli x,lli n)
{
    lli res=1;
    while(n>0)
    {
        if(n%2==1)
        {
            res=(res*x)%mod;
        }
        x=(x*x)%mod;
        n=n/2;
    }
    return  res%mod;
}

lli factorialNumInverse[1000001];
lli naturalNumInverse[1000001];
lli fact[1000001];

void InverseofNumber(lli n,lli p)
{
    naturalNumInverse[0] = naturalNumInverse[1] = 1;
    for (lli i = 2; i <= n; i++)
        naturalNumInverse[i] = naturalNumInverse[p % i] * (p - p / i) % p;
}
void InverseofFactorial(lli n,lli p)
{
    factorialNumInverse[0] = factorialNumInverse[1] = 1;

    for (lli i = 2; i <= n; i++)
        factorialNumInverse[i] = (naturalNumInverse[i] * factorialNumInverse[i - 1]) % p;
}
void factorial(lli n,lli p)
{
    fact[0] = 1;
    for (lli i = 1; i <= n; i++) {
        fact[i] = (fact[i - 1] * i) % p;
    }
}
lli nCrModp(lli n, lli r, lli p)
{
    lli ans = ((fact[n] * factorialNumInverse[r]) % p * factorialNumInverse[n - r]) % p;
    return ans;
}


