const int N=1e6+5;
const ll inf=1ll<<60;
ll dp[2][13][1<<13],k;
int w[N],fz[N];
ll a[N],p[13],np[13],b[N],Cnt[1<<13];
int c[13],nc[13],cnt,S,now=1,id[N];
ll gcd(ll a,ll b){return b?gcd(b,a%b):a;}
inline void cmin(ll &x,ll y){if(x>y)x=y;}

inline void calc(int w)
{
	for(int i=1;i<=cnt;++i)
		for(int j=0;j<=S;++j)
			dp[now][i][j]=dp[now^1][i][j];
	static int fz[1<<12];
	for(int i=1;i<=S;++i)
	{
		ll res=1;
		for(int j=0;j<cnt;++j)if(i>>j&1)res*=np[j+1];
		fz[i]=res<=k;
	}
	for(int i=1;i<=S;++i)
	{
		if(!fz[i])continue;
		if(++Cnt[i]>cnt)continue;
		cmin(dp[now][1][i],w);
		for(int t=S^i,r=t;;t=(t-1)&r)
		{
			for(int j=2;j<=cnt;++j)
				cmin(dp[now][j][t|i],dp[now^1][j-1][t]/(j-1)*j+1ll*j*w);
			if(!t)break;
		}
