#include <iostream>
#include <stdio.h>
#include <numeric>
#include <queue>
#include <string>
#include <cmath>
#include <vector>
#include <deque>
#include <iomanip>
#include <set>
#include <queue>
#include <bitset>
#include <iomanip>
#include <map>
#include <string.h>
#include <stack>
#include <algorithm>

using namespace std;

bool graph[1010][1010];
int values[1010];		// 0: a, 1: b, 2: c
bool vis[1010];

void bfs(int pos , int n)
{
	// 0,2
	queue<int> q;
	q.push(pos);
	values[pos] = 0;
	vis[pos] = true;

	while (!q.empty())
	{
		int cur = q.front();
		q.pop();

		for (int i = 1;i <= n;i ++)
		{
			if (!graph[cur][i] && !vis[i])
			{
				vis[i] = true;
				values[i] = 1 - values[cur];
				q.push(i);
			}
		}
	}
}

bool check(int n)
{
	int i , j;
	for (i = 1;i <= n;i ++)
		for (j = i + 1;j <= n;j ++)
		{
			if (graph[i][j])
			{
				if ((values[i] == 0 && values[j] == 2)
				 || (values[i] == 2 && values[j] == 0))
				 return false;
			}
			else
			{
				if ((values[i] == 0 && values[j] == 2) 
				 || (values[i] == 2 && values[j] == 0))
				 continue;
				else
					return false;
			}
		}
	return true;
}

int main()
{
	int i , j , n , m;
	while (scanf("%d%d" , &n , &m) != EOF)
	{
		memset(values , - 1 , sizeof(values));
		memset(vis , 0 , sizeof(vis));
		memset(graph , 0 , sizeof(graph));
		for (i = 0;i < m;i ++)
		{
			int u , v;
			scanf("%d%d" , &u , &v);
			graph[u][v] = graph[v][u] = true;
		}
		for (i = 1;i <= n;i ++)
		{
			if (!vis[i])
				bfs(i , n);
		}
		if (check(n))
		{
			printf("Yes\n");
			for (i = 1;i <= n;i ++)
			{
				char ch = values[i] + 'a';
				printf("%c" , ch);
			}
			printf("\n");
		}
		else
			printf("No\n");
	}
	return 0;
}

