struct UnionFind {
  vi set_;
  UnionFind(int size) : set_(size, -1){ }

  void add(int a, int b) {
    int rtA = root(a);
    int rtb = root(b);
    if (rtA == rtb) {
      return;
    }
    set_[rtA] += set_[rtb];
    set_[rtb] = rtA;
  }

  bool find(int a, int b) { return root(a) == root(b); }

  int root(int a) {
    if (set_[a] < 0) {
      return a;
    } else {
      set_[a] = root(set_[a]);
      return set_[a];
    }
  }

  int size(int a) { return -set_[root(a)]; }
};

