int L[N<<5], R[N<<5], Lis[N<<5], Lds[N<<5];

void update(int *a, int &o, int l, int r, int x, int v) {
	if (!o) o=++tot;
	a[o] = max(a[o], v);
	if (l!=r) ((l+r)>>1)>=x?update(a,L[o],l,((l+r)>>1),x,v):update(a,R[o],((l+r)>>1)+1,r,x,v);
}
int query(int *a, int o, int l, int r, int ql, int qr) {
	if (!o||ql<=l&&r<=qr) return a[o];
	if (((l+r)>>1)>=qr) return query(a,L[o],l,((l+r)>>1),ql,qr);
	if (((l+r)>>1)<ql) return query(a,R[o],((l+r)>>1)+1,r,ql,qr);
	return max(query(a,L[o],l,((l+r)>>1),ql,qr),query(a,R[o],((l+r)>>1)+1,r,ql,qr));
}
int merge(int x, int y) {
	if (!x||!y) return x+y;
	ans = max(ans, Lis[L[x]]+Lds[R[y]]);
	ans = max(ans, Lis[L[y]]+Lds[R[x]]);
	Lis[x] = max(Lis[x],Lis[y]);
	Lds[x] = max(Lds[x],Lds[y]);
	L[x] = merge(L[x],L[y]);
	R[x] = merge(R[x],R[y]);
	return x;
}

