		dfs(y,x);
		int lis1 = a[x]==1?0:query(Lis,T[y],1,*b,1,a[x]-1);
		int lds1 = a[x]==*b?0:query(Lds,T[y],1,*b,a[x]+1,*b);
		ans = max(ans, lis1+lds+1);
		ans = max(ans, lds1+lis+1);
		T[x] = merge(T[x], T[y]);
		lis = max(lis, lis1);
		lds = max(lds, lds1);
	}
	update(Lis,T[x],1,*b,a[x],lis+1);
	update(Lds,T[x],1,*b,a[x],lds+1);
}

