#include <iostream>
#include <sstream>
#include <stdio.h>
#include <memory.h>
#include <algorithm>
#include <set>
#include <map>
#include <string>
#include <vector>
#include <cmath>
#include <queue>
#include <deque>
#include <stack>
#include <cassert>

using namespace std;

#define mp make_pair
#define pb push_back
#define _(a,b) memset( (a), b, sizeof( a ) )
#define all(a) a.begin(), a.end()
#define sz(a) (int)a.size()

typedef unsigned long long ull;
typedef long long lint;
typedef pair < int , int > pii;
typedef long double ld;

const int inf = 1000 * 1000 * 1000;
const lint linf = 1000000000000000000LL;
const double eps = 1e-9;

void prepare( )
{
#ifdef _DEBUG
	freopen("input.txt", "r", stdin);
#endif
}

const int pred = 350;
const int after = 360;
int n;
int w[105];
int dp[105][355];
int p[105][355];
vector < int > q;
bool used[105];
vector < int > cost;

bool solve( )
{
	cin >> n;
	for (int i = 0; i < n; i ++)
		cin >> w[i];
	_(dp,0);
	for (int i = 0; i < n; i ++)
	{
		for (int j = pred; j >= 0; j --)
		{
			if (j - w[i] >= 0)
			{
				if (dp[i+1][j] < dp[i][j - w[i]] + 1)
				{
					dp[i+1][j] = dp[i][j - w[i]] + 1;
					p[i+1][j] = 1;
				}
			}
		}
	}

	int mx = 0;
	int bst = 0;
	for (int i = 0; i <= pred; i ++)
	{
		if (dp[n][i] > mx)
		{
			mx = dp[n][i];
			bst = i;
		}
	}

	int v = n;
	int tt = bst;
	_(used,false);
	while (v)
	{
		if (p[v][tt])
		{
			used[v-1] = true;
			q.pb(v-1);
			tt = tt - w[v - 1];
		}
		v--;
	}

	for (int i = 0; i < n; i ++)
		if (!used[i])
			cost.pb(w[i]);
	sort(all(cost));

	int sum = 0;
	for (int i = 0; i < sz(cost); i ++)
	{
		bst += cost[i];
		if (bst <= pred + after)
		{
			mx++;
			sum += max(0,bst - pred);
		}
	}

	cout << mx << " " << sum << endl;

	return false;
}

int main()
{
	prepare( );
	while ( solve() );
	return 0;
}

