ll calc(ll x)
{
   ll lef=0,rig=2e4,ans=-1;
   while(lef<=rig)
   {
       ll mid=(lef+rig)>>1;
       if(x- (mid* (mid+1) )/2 >=0) ans=mid,lef=mid+1;
       else rig=mid-1;
   }
   ll res=(ans+1)*x-(ans*(ans+1)*(ans+2))/6;
   return res;
}


